DROP DATABASE IF EXISTS BD_JULYBARRETO;
CREATE DATABASE IF NOT EXISTS BD_JULYBARRETO;

USE BD_JULYBARRETO;

CREATE TABLE IF NOT EXISTS Centro_de_Acopio(
    ID_Centro_Acopio INT UNSIGNED PRIMARY KEY,
    Nombre_Centro_Acopio VARCHAR(60) NOT NULL,
    Tipo VARCHAR(60) NOT NULL,
    Necesidades VARCHAR(80) NOT NULL,
    Nombre_Contacto VARCHAR(50) NOT NULL,
    Telefono INT NOT NULL,
    Calle VARCHAR(50) NOT NULL,
    Horarios VARCHAR(50) NOT NULL,
    Tipo_Centro_Acopio VARCHAR(70) NOT NULL
);

CREATE TABLE IF NOT EXISTS Ubicacion(
    ID_Exterior INT UNSIGNED NOT NULL,
    ID_Centro_Acopio_FK INT UNSIGNED NOT NULL,
    Colonia VARCHAR(50) NOT NULL,
    Municipio VARCHAR(50) NOT NULL,
    Ciudad VARCHAR(50) NOT NULL,
    Direccion VARCHAR(70) NOT NULL,
    FOREIGN KEY (ID_Centro_Acopio_FK) REFERENCES Centro_de_Acopio(ID_Centro_Acopio)
);

CREATE TABLE IF NOT EXISTS Datos_de_Ingreso(
    ID_Ingresos INT UNSIGNED NOT NULL,
    ID_Exterior_FK INT UNSIGNED NOT NULL,
    Verificado_por VARCHAR(60) NOT NULL,
    Fecha_Actualizacion DATE NOT NULL,
    Actualizado_por VARCHAR(70) NOT NULL,
    FOREIGN KEY (ID_Exterior_FK) REFERENCES Ubicacion(ID_Exterior)
);


